import React, {useContext, useEffect, useState} from 'react';
import "../css/contact.css";
import {base_url, characters, defaultHero, navNames, period_month, StarWarsContext} from "../utils/constants";
import {useNavigate, useParams} from "react-router-dom";
import {withHeroId} from "../hoc/withHeroId";

const Contact =({heroId})=>{
    const [planets,setPlanets] = useState(['wait...']);

    const fillPlanets = async (url) => {
        const response = await fetch(url);
        const json = await response.json();
        const planets = json.map(item => item.name);
        setPlanets(planets);
        const info = {
            payload: planets,
            time: Date.now()
        }
        sessionStorage.setItem('planets',JSON.stringify(info));
    }

    useEffect(()=>{

        changeHero(heroId);
        const storedPlanets = JSON.parse(sessionStorage.getItem('planets'));
        if(storedPlanets && (Date.now()-storedPlanets.time) < period_month){
            setPlanets(storedPlanets.payload);
        }
        else {
            fillPlanets(`${base_url}/v1/planets`);
        }
    }, [])


    return (
        <div>
            <form onSubmit={(e) => {
                e.preventDefault();
            }}>
                <label>First Name
                    <input type="text" name="firstname" placeholder="Your name.."/>
                </label>
                <label>Planet
                    <select name="planet">{
                        planets.map((item, index) => <option value={item} key={index}>{item}</option>)
                    }
                    </select>
                </label>
                <label>Subject
                    <textarea name="subject" placeholder="Write something.."/>
                </label>
                <input type="submit" value="Submit"/>
            </form>
        </div>
    )
}

export default CwithHeroId(navNames[3].route)(Contact)
